import React, { useState, useEffect } from "react";
import {
  SafeAreaView,
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  Linking,
  Dimensions,
} from "react-native";
import axios from "axios";

const { width } = Dimensions.get('window');
const cardWidth = width * 0.8; // 80% of screen width

const App = () => {
  const [scheduleData, setScheduleData] = useState({
    monday: { time: [], event_name: [], location: [] },
    tuesday: { time: [], event_name: [], location: [] },
    wednesday: { time: [], event_name: [], location: [] },
    thursday: { time: [], event_name: [], location: [] },
    friday: { time: [], event_name: [], location: [] },
  });
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => setCurrentTime(new Date()), 60000);
    return () => clearInterval(interval);
  }, []);

  const currentDate = currentTime.toLocaleString("en-US", { timeZone: "Australia/Sydney" });
  const currentDay = new Intl.DateTimeFormat('en-US', { weekday: 'long', timeZone: "Australia/Sydney" }).format(currentTime).toLowerCase();
  const currentHour = currentTime.getHours();
  const currentMinute = currentTime.getMinutes();

  const openGoogleMaps = (location) => {
    // ... (keep the existing openGoogleMaps function)
  };

  const isCurrentEvent = (eventTime, eventDay) => {
    // ... (keep the existing isCurrentEvent function)
  };

  useEffect(() => {
    const fetchScheduleData = async () => {
      try {
        const response = await axios.get('https://free-food-monash-api-337668b48c95.herokuapp.com/json-object');
        setScheduleData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchScheduleData();
  }, []);

  return (
    <SafeAreaView style={{ flex: 1, backgroundColor: '#FF6900' }}>
      <View style={{ alignItems: 'center', paddingVertical: 10 }}>
        <Text style={{ fontSize: 28, fontWeight: 'bold', color: 'white', fontFamily: 'Helvetica' }}>
          Monash Free Food Guide
        </Text>
      </View>
      <ScrollView contentContainerStyle={{ alignItems: 'center', paddingBottom: 16 }}>
        {Object.entries(scheduleData).map(([day, data]) => (
          <View key={day} style={{ marginBottom: 18, width: cardWidth }}>
            <Text style={{ fontSize: 20, fontWeight: 'bold', color: 'white', marginBottom: 8, textAlign: 'center' }}>
              {day.charAt(0).toUpperCase() + day.slice(1)}
            </Text>
            {data.time.map((time, index) => (
              <View
                key={index}
                style={{
                  marginBottom: 8,
                  borderRadius: 10,
                  overflow: 'hidden',
                  backgroundColor: isCurrentEvent(time, day) ? '#4CAF50' : 'white',
                  width: '100%',
                }}
              >
                {isCurrentEvent(time, day) && (
                  <Text style={{ fontSize: 16, fontWeight: 'bold', color: 'white', padding: 8, backgroundColor: '#4CAF50' }}>
                    Happening now!
                  </Text>
                )}
                <View style={{ padding: 12 }}>
                  <Text style={{ fontSize: 16, fontWeight: 'bold', color: isCurrentEvent(time, day) ? 'white' : 'black' }}>
                    {time}
                  </Text>
                  <Text style={{ fontWeight: 'bold', color: isCurrentEvent(time, day) ? 'white' : 'black', marginTop: 4 }}>
                    {data.event_name[index]}
                  </Text>
                  <TouchableOpacity onPress={() => openGoogleMaps(data.location[index])}>
                    <Text style={{ color: isCurrentEvent(time, day) ? 'lightblue' : 'blue', textDecorationLine: 'underline', marginTop: 4 }}>
                      {data.location[index]}
                    </Text>
                  </TouchableOpacity>
                </View>
              </View>
            ))}
          </View>
        ))}
      </ScrollView>
      <View style={{ padding: 8, alignItems: 'center' }}>
        <Text style={{ color: 'white' }}>Created by AmirÂ©</Text>
      </View>
    </SafeAreaView>
  );
};

export default App;
